function hamburger(){$(".hamburger").click(function(){$(this).parent().parent().toggleClass("active"),$("body, html").toggleClass("hidden")})}function scrollTo(){$("ul li a[data-link]").click(function(t){t.preventDefault(),$(".nav-bar").removeClass("active"),$("html, body").removeClass("hidden");let l=$(this).attr("data-link"),o=$(".nav-bar").innerHeight()-3;$("html, body").stop().animate({scrollTop:$('section[data-section="'+l+'"]').offset().top-o},10)});let t=$(".scroll-btn");$(window).scroll(function(){let l=$(this).scrollTop();console.log(),l>=$(this).height()?(console.log("metia"),t.addClass("active")):(console.log("naklebia"),t.removeClass("active"))}),t.click(function(t){t.preventDefault(),$("body,html").animate({scrollTop:$("html,body").offset().top},10)})}$(function(){hamburger(),scrollTo()});